import { useState } from "react";
import { v4 as uuidv4 } from "uuid";
import videos from "../data/videos.json";

import VideoList from "./components/VideoList";
import NavBar from "./components/NavBar";

function Main() {
    const videoList = videos.map();

    const [videoData, setVideoData] = useState(videoList);
    const [currentVideo, setCurrentVideo] = useState(videoList[0]);

    const changeVideo = (id) => {
      const selectedVideo = videoData.find((video) => {
        return video.id === id;
      });

      setCurrentVideo(selectedVideo);
    };

    const addCard = (title, content) => {
      const newCard = {
        id: uuidv4(),
        title,
        content,
      };
      const newCardData = [...cardData, newCard];
      setCardData(newCardData);
    };

    return (
      <>
        <NavBar />
        <VideoList
          activeVideoId={currentVideo.id}
          changeActiveVideo={changeVideo}
          videoData={videoData}
        />
      </>
    );
  }

  export default Main;
