import { useState } from "react";
import videos from '../data/video-details.json'

import VideoList from "./VideoList";
import CurrentVideo from "./CurrentVideo";
import CommentList from "./CommentList";
import './Main.scss';

function Main() {
    const videoList = videos;

    const [videoData, setVideoData] = useState(videoList);
    const [currentVideo, setCurrentVideo] = useState(videoList[0]);

    const changeVideo = (id) => {
      const selectedVideo = videoData.find((video) => {
        return video.id === id;
      });

      setCurrentVideo(selectedVideo);
    };


    const updateCard = () => {
      const newVideoData = [...videoData];
      setVideoData(newVideoData);
    };



    return (
      <>
        <div className="main__desktop">
          <div className="main__desktop--container">
            <CurrentVideo currentVideo={currentVideo} videoData={videoData}/>

          </div>
          <div className="main__desktop--container">

          </div>
        </div>

      </>
    );
  }

  export default Main;
