export default function Comment({name, timestamp, comment}) {

    return (
    <div className="comment">
        <img className="comment__avatar"/>
        <div className="comment__details">
            <div className="comment__details--header">
                <h3 className="comment">{name}</h3>
                <h5 className="comment__avatar">{new Date(timestamp).toLocaleDateString()}</h5>
            </div>

        </div>


        <h4 className="comment__avatar">{comment}</h4>
    </div>
    );
}
